        padding: '2px 10px',
        fontSize: '0.9rem',
        width: '20px',

    }
}


class BubbleAlert extends Component {
    getNumber(){
        if(!n ){ return(' ') }
        return n>9 ? '9+' : n
    }


    render() {
        const {value}=this.props
        return (
            <span style={styles.bubbleStyle}>
                {this.getNumber(value)}
            </span>
        )
    }
}

export default BubbleAlert
