import {Component} from 'react'


const styles={
    detallesCarro:{
        backgroundColor: '#fff',
        position:'absolute',
        marginTop: '30px',
        boxShadow: '1px 5px 5px rgb(0, 0, 0, 0.3)',
        width:'300px',
        right: '50px',
    },
    ul:{

    },
    producto:{
        listStyle: 'none',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '25px 20px',
        borderBottom:'1px solid #aaa'
    }
}



class DetallesCarro extends Component {
    render(){
        const {carro}=this.props
        console.log(carro)
        return (
            <div style={styles.detallesCarro}>
                <ul style={styles.ul}>
                    {carro.map(x=> 
                        <li style={styles.producto} key={x.name}>
                        <img alt={x.name} src={x.img} width='50px' height='32px' />
                        {x.name} <span>{x.cantidad}</span></li>
                    )}
                </ul>
            </div>
        )
    }
}

export default DetallesCarro
